import * as Styled from './styles'

export const Header = () => {
  const auth = useContext(AuthContext)
  const navigate = useNavigate()

  const logout = async () => {
    await auth.singout()
    goLogin(navigate)
  }

  return (
    <Styled.Header>
      <Styled.Title>Desafio React - Login</Styled.Title>
      <button className='btn btn-danger' onClick={logout}>Sair</button>
    </Styled.Header>
  )
}
