import styled from 'styled-components'

export const Header = styled.header`
  width: 100%;
  height: 70px;
  padding: 0 60px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
`

export const Title = styled.h2``
