import { goLogin } from '../routes/Coordinator'
import { useNavigate } from 'react-router-dom'
import { useEffect } from 'react'

export const useProtectedPage = () => {
  const navigate = useNavigate()

  useEffect(() => {
    const token = localStorage.getItem('token')

    if (token === null) {
      goLogin(navigate)
    }
  }, [navigate])
}
