import styled from 'styled-components'

export const Login = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`

export const Form = styled.form`
  width: 400px;
  height: 300px;
  border-radius: 6px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`

export const Title = styled.h2`
  margin-bottom: 40px;
  color: #fff;
`

export const Button = styled.button``
